import { Component, OnInit } from '@angular/core';
import { StudentService } from '../student.service';
import { Student } from '../student';
import { StudentPipe } from '../student.pipe';
import { filter } from 'rxjs';

@Component({
  selector: 'app-student-grid',
  templateUrl: './student-grid.component.html',
  styleUrls: ['./student-grid.component.css'],
})
export class StudentGridComponent implements OnInit {
  students: Student[] = [];
  searchTerm: string = '';

  constructor(private studentService: StudentService) {}

  ngOnInit() {
    this.getStudents();
  }

  getStudents() {
    this.studentService.getStudents().subscribe(
      (students: Student[]) => {
        this.students = students;
      },
      (error: any) => {
        console.error(error);
      },
    );
  }

  onClearSearch() {
    this.searchTerm = '';
  }

  protected readonly filter = filter;
  protected readonly filter = filter;
}
<h1>Student Grid</h1>
<div class="row">
  <div class="col-md-6">
    <input type="text" [(ngModel)]="searchTerm" placeholder="Search students...">
  </div>
  <div class="col-md-6">
    <button type="button" (click)="onClearSearch()">Clear search</button>
  </div>
</div>
<div class="row">
  <div class="col-md-12">
    <table class="table table-bordered">
      <thead>
      <tr>
        <th>First Name</th>
        <th>Last Name</th>
        <th>Mobile</th>
        <th>Email</th>
        <th>NIC</th>
      </tr>
      </thead>
      <tbody>
      <tr *ngFor="let student of students | async | filter:searchTerm">
        <td>{{ student.firstName }}</td>
        <td>{{ student.lastName }}</td>
        <td>{{ student.mobile }}</td>
        <td>{{ student.email }}</td>
        <td>{{ student.nic }}</td>
      </tr>
      </tbody>
    </table>
  </div>
</div>
